program DrawPentagon;

var
  i: Integer;

begin
  SetPenColor('#FF1493');
  SetPenWidth(2);

  for i := 1 to 5 do
  begin
    Forward(80);
    TurnRight(72);
  end;

  WriteLn('Pentagon complete!');
end.
